mapper.update:
UPDATE ORDER_MAIN 
SET ORDER_STATUS = 2 
WHERE ORDER_NO = #{orderNoInt}

UPDATE ORDER_MAIN 
SET ORDER_STATUS = 5 
WHERE ORDER_NO = #{orderNoInt}

UPDATE EMPLOYEE 
SET EMP_DELIVERYSTATUS = 'N', EMP_DELIVERINGONO =  #{empDeliveryONo} 
WHERE EMP_NO =  #{employeeNo}

UPDATE ORDER_MAIN 
SET ORDER_STATUS = 3 
WHERE ORDER_NO = #{orderNoInt}

UPDATE ORDER_MAIN 
SET ORDER_STATUS = 4 
WHERE ORDER_NO = #{orderNoInt}

UPDATE EMPLOYEE 
SET EMP_DELIVERYSTATUS = 'Y', EMP_DELIVERINGONO = 0 
WHERE EMP_DELIVERINGONO = #{orderNoInt}

UPDATE ORDER_MAIN 
SET ORDER_STATUS = 5 
WHERE ORDER_NO = #{orderNoInt}

UPDATE ORDER_MAIN 
SET ORDER_STATUS = 6 
WHERE ORDER_NO = #{orderNoInt}

mapper.select:
SELECT *
FROM EMPLOYEE
WHERE COM_NO = #{ comNo }

mapper.update:
UPDATE STOCK 
SET STOCK_WEIGHT  = (SELECT STOCK_WEIGHT FROM STOCK WHERE MATERIAL_NO = #{materialNo}) + #{materialWeight} 
WHERE MATERIAL_NO = #{materialNo} 
AND COM_NO = #{comNo}

mapper.select:
SELECT SALES_NO, COM_NO, SALES_INPUTDATE, SALES_CATEG, EXPENSE_NO, ORDER_NO, SALES_PRICE
FROM SALES
WHERE COM_NO = #{comNo}
